import React, { useEffect, useState } from 'react';
import agent from '../api/agent';

const TrainerCard = ({ trainer }) => {
  return (
    <div style={{ width: '500px', margin: '8px', borderRadius: '16px', overflow: 'hidden' }}>
      {/* Antrenör Fotoğrafı */}
      <div style={{ height: '200px', width: '100%', borderRadius: '16px 16px 0 0', overflow: 'hidden' }}>
        <img
          src={trainer.trainerImageUrl}
          alt={trainer.trainerName}
          style={{ width: '100%', height: '100%', objectFit: 'cover' }}
        />
      </div>
      {/* Antrenör Adı */}
      <div style={{ padding: '8px' }}>
        <div style={{ fontSize: '16px', fontWeight: 'bold', color: 'black', textAlign: 'center' }}>
          {trainer.trainerName}
        </div>
      </div>
      {/* Antrenör Tablosu */}
      <div style={{ height: '100%', padding: '8px' }}>
        <table style={{ width: '100%' }}>
          <thead>
            <tr>
              <th>Gün</th>
              <th>Başlangıç Saati</th>
              <th>Bitiş Saati</th>
            </tr>
          </thead>
          <tbody>
            {trainer.daySchedules.map(schedule => (
              <tr key={schedule.day}>
                <td>{/* You can implement getDayName function here if needed */}</td>
                <td>{schedule.startTime}</td>
                <td>{schedule.endTime}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default function TrainerList() {
  const [trainers, setTrainers] = useState([]);

  useEffect(() => {
    agent.Trainer.list()
      .then(response => setTrainers(response))
      .catch(error => console.error('Error fetching trainers:', error));
  }, []);

  return (
    <div style={{ height: '500px', overflowX: 'auto', whiteSpace: 'nowrap' }}>
      {trainers.map(trainer => (
        <TrainerCard key={trainer.id} trainer={trainer} />
      ))}
    </div>
  );
}
