// CourseDetails.js

import React, { useEffect, useState } from 'react';
import agent from '../api/agent';

export default function CourseDetails({ match }) {
  const courseId = match.params.id;
  const [courseDetails, setCourseDetails] = useState(null);

  useEffect(() => {
    agent.SpecialCourse.details(courseId)
      .then(details => setCourseDetails(details))
      .catch(error => console.error('Error fetching course details:', error));
  }, [courseId]);

  if (!courseDetails) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <h1>{courseDetails.specialCourseName}</h1>
      <img src={courseDetails.specialCourseImgUrl} alt={courseDetails.specialCourseName} />
      <p>{courseDetails.specialCourseDefinition}</p>
      {/* Render other course details here */}
    </div>
  );
}
