import React, { useState, useEffect } from 'react';
import { experimentalStyled as styled } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import { Card, CardActionArea, CardMedia, CardContent, Typography } from '@mui/material';

const Item = styled(Card)(({ theme }) => ({
  background: 'white',
  maxWidth: 345,
  padding: theme.spacing(2),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));

function PromotionCards() {
  const [data, setData] = useState([]);

  useEffect(() => {
    const fetchData = () => {
      try {
        fetch('http://192.168.238.54:5280/apii/HomePageCards/allcards')
          .then(response => {
            if (!response.ok) {
              throw new Error(`HTTP error! Status: ${response.status}`);
            }
            return response.json();
          })
          .then(result => {
            console.log('API Response:', result);
            setData(result);
          })
          .catch(error => {
            console.error('Error fetching data:', error);
          });
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []); // Dependency array is empty to run this effect only once when the component mounts

  return (
    <Box sx={{ flexGrow: 1 }}>
      <Grid container spacing={{ xs: 2, md: 3 }} columns={{ xs: 4, sm: 8, md: 12 }}>
        {data.map((item, index) => (
          <Grid item xs={2} sm={4} md={4} key={index}>
            <Card sx={{ maxWidth: 345 }}>
              <CardActionArea>
                <CardMedia
                  component="img"
                  height="140"
                  image={item.homePageCardsImage}
                  alt="Card Image"
                />
                <CardContent>
                  <Typography gutterBottom variant="h5" component="div">
                    {item.homePageCardsContent}
                  </Typography>
                </CardContent>
              </CardActionArea>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
}

export default PromotionCards;
