import './App.css';
import Registration from './Registration';
import Login from './Login';
import theme from "./theme";
import { ThemeProvider } from '@emotion/react';
import CssBaseline from '@mui/material/CssBaseline';
import ResponsiveAppBar from './components/appbar.js';
import Anasayfa from "./pages/Anasayfa.js"


import React, { useState, useEffect } from 'react';

function App() {
  const [cards, setCards] = useState([]);

  useEffect(() => {
    // Fetch the data when the component mounts
    fetchCards();
  }, []);

  const fetchCards = async () => {
    try {
      const response = await fetch('http://http://localhost:54104//apii/HomePageCards/allcards');
      const data = await response.json();
      setCards(data);
    } catch (error) {
      console.error('Error fetching cards:', error);
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <div className="App">
        {/* Render your cards here */}
        {cards.map(card => (
          <div key={card.homePageCardsId}>
            <img src={card.homePageCardsImage} alt={card.homePageCardsContent} />
            <p>{card.homePageCardsContent}</p>
          </div>
        ))}
      </div>
    </ThemeProvider>
  );
}

export default App;
