import React, { useEffect, useState } from 'react';
import { FormControl, InputLabel, Select, MenuItem } from '@mui/material';
import ResponsiveAppBar from '../components/appbar';
import agent from '../api/agent';

export default function Prices() {
  const [prices, setPrices] = useState([]);
  const [selectedPaymentType, setSelectedPaymentType] = useState('');
  const [coursePrices, setCoursePrices] = useState([]);

  useEffect(() => {
    agent.Prices.list()
      .then(prices => setPrices(prices))
      .catch(error => console.error('Error fetching prices:', error));
  }, []);

  const [paymentTypes, setPaymentTypes] = useState([]);

  useEffect(() => {
    agent.Prices.paymentDetail()
      .then(paymentTypes => setPaymentTypes(paymentTypes))
      .catch(error => console.error('Error fetching payment types:', error));
  }, []);

  const handlePaymentTypeChange = (event) => {
    const selectedTypeId = event.target.value;
    setSelectedPaymentType(selectedTypeId);

    // Filter course prices based on the selected payment type
    const filteredCoursePrices = prices.filter(coursePrice => coursePrice.paymentTypeId === selectedTypeId);
    setCoursePrices(filteredCoursePrices);
  };

  return (
    <div>
      <ResponsiveAppBar />
      <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-around' }}>
        <FormControl fullWidth>
          <InputLabel id="payment-type-label">Payment Type</InputLabel>
          <Select
            labelId="payment-type-label"
            id="payment-type-select"
            value={selectedPaymentType}
            onChange={handlePaymentTypeChange}
            label="Payment Type"
          >
            {paymentTypes.map((paymentType) => (
              <MenuItem key={paymentType.id} value={paymentType.id}>
                {paymentType.paymentTypeName}
              </MenuItem>
            ))}
          </Select>
        </FormControl>

        {coursePrices && coursePrices.length > 0 ? (
          coursePrices.map(data => (
            <p key={data.id}>{data.price}</p>
          ))
        ) : (
          <p>No prices available for the selected payment type</p>
        )}
      </div>
    </div>
  );
}
