import React, { useState, useEffect } from 'react';
import { experimentalStyled as styled } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import { Card, CardActionArea, CardMedia, CardContent, Typography } from '@mui/material';
import axios from "axios";

const Item = styled(Card)(({ theme }) => ({
  background: 'white',
  maxWidth: 200,
  padding: theme.spacing(2),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));


function Promotion_cards() {
  const [data, setData] = useState([]);
/*
  useEffect(()=>{
    (async()=>await Load())();
  },[]);

  async function Load(){
    const result= await axios.get("http://localhost:54104/apii/HomePageCards/allcards");
    setData(result.data);
    console.log(result.data);
  }*/

  useEffect(() => {
    // Fetch data from the API when the component mounts
    fetch("http://localhost:54104/apii/HomePageCards/allcards",{
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        // Diğer headerlar buraya eklenebilir
      },})
      .then((response) => response.json())
      .then((result) => {
        console.log('API Response:', result); 
        setData(result);
      })
      .catch((error) => console.error('Error fetching data:', error));
  }, []);

  return (
    <Box sx={{ flexGrow: 1 }}>
      <Grid container spacing={{ xs: 2, md: 3 }} columns={{ xs: 4, sm: 8, md: 12 }}>
        {data.map((item, index) => (
          <Grid item xs={2} sm={4} md={4} key={index}>
            <Card sx={{ maxWidth: 345 }}>
              <CardActionArea>
               
                <CardContent>
                  <Typography variant="body2" color="text.secondary">
                    {item.homePageCardsContent}
                  </Typography>
                </CardContent>
              </CardActionArea>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
}

export default Promotion_cards;
