import axios from "axios";

axios.defaults.baseURL = "http://localhost:63652/api/";

axios.defaults.withCredentials = true; // Bu sat覺r覺 ekleyin

const responseBody = (response) => response.data;

const requests = {
  get: (url) => axios.get(url).then(responseBody),
  post: (url, body) => axios.post(url, body).then(responseBody),
  put: (url, body) => axios.put(url, body).then(responseBody),
  delete: (url) => axios.delete(url).then(responseBody),
};

const Facilities = {
  list: () => requests.get("General/facilities"),
  details: (id) => requests.get(`General/${id}`),
};

const Account={
  login: (values) => {
    // Add headers to the POST request
    const headers = {
      'Content-Type': 'application/json',
    };

    // Make the POST request with the specified headers
    return requests.post('Account/login', values, { headers });
  },  register:(values)=>requests.post('Account/register',values),
    currentUser:()=>requests.get('Account/currentUser'),

}

const agent = {
  Facilities,
  Account,
};

export default agent;
