// CourseDetails.js
import React, { useEffect, useState } from 'react';
import agent from '../api/agent';
import { useParams } from 'react-router-dom';


export default function CourseDetails() {
  const { id } = useParams();
  const [courseDetails, setCourseDetails] = useState(null);

  useEffect(() => {
    const courseId = parseInt(id);
    console.log('Course ID:', courseId);


    
    id && agent.SpecialCourse.details(courseId)
      .then(details => {
        console.log('API Response:', details);

        setCourseDetails(details);
      })
      .catch(error => console.error('Error fetching course details:', error.response));
    }, [id]);

  console.log('Course Details:', courseDetails);

  if (!courseDetails) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <h1>{courseDetails?.specialCourseName}</h1>
      <img src={courseDetails?.specialCourseImgUrl} alt={courseDetails?.specialCourseName} />
      <p>{courseDetails?.specialCourseDefinition}</p>
      {/* Render other course details here */}
    </div>
  );
}
