import React, { useState, useEffect } from 'react';
import { experimentalStyled as styled } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Grid from '@mui/material/Grid';
import { Card, CardActionArea, CardMedia, CardContent, Typography } from '@mui/material';

const Item = styled(Card)(({ theme }) => ({
  background: 'white',
  maxWidth: 200,
  padding: theme.spacing(2),
  textAlign: 'center',
  color: theme.palette.text.secondary,
}));

function Promotion_cards({ onDataFetched }) {
  const [data, setData] = useState([]);

  useEffect(() => {
    fetch('http://localhost:54104/apii/HomePageCards/allcards', {
      method: 'GET',
    })
      .then(response => response.json())
      .then(data => {
        console.log('API Response:', data);
        setData(data);
        onDataFetched(data); // onDataFetched fonksiyonu ile verileri Anasayfa bileşenine iletiyoruz
      })
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  return (
    <Box sx={{ flexGrow: 1 }}>
      <Grid container spacing={{ xs: 2, md: 3 }} columns={{ xs: 4, sm: 8, md: 12 }}>
        {data.map((item, index) => (
          <Grid item xs={2} sm={4} md={4} key={index}>
            <Card sx={{ maxWidth: 345 }}>
              <CardActionArea>
                <CardContent>
                  <Typography variant="body2" color="text.secondary">
                    {item.homePageCardsContent}
                  </Typography>
                </CardContent>
              </CardActionArea>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
}

export default Promotion_cards;
