// CourseDetails.js
import React, { useEffect, useState } from 'react';
import agent from '../api/agent';
import { useParams } from 'react-router-dom';
import ResponsiveAppBar from '../components/appbar';


export default function CourseDetails() {
  const { id } = useParams();
  const [courseDetails, setCourseDetails] = useState(null);
  const courseId = parseInt(id);

  useEffect(() => {
    agent.SpecialCourse.details(courseId)
      .then(details => {
        console.log('API Response:', details);
        setCourseDetails(details);
      })
      .catch(error => console.error('Error fetching course details:', error.response));
  }, [id]);


  if (!courseDetails) {
    return <p>Loading...</p>;
  }

  return (
    <div>
        <ResponsiveAppBar/>
      <div style={{ width: '100%', height: '30vh', position: 'relative' }}>
        <img
          src={courseDetails?.specialCourseImgUrl}
          alt={courseDetails?.specialCourseName}
          style={{ width: '100%', height: '100%', objectFit: 'cover' }}
        />
        <div
          style={{
            position: 'absolute',
            bottom: 0,
            left: 0,
            padding: '20px',
            background: 'rgba(0, 0, 0, 0.5)',
            color: 'white',
            textTransform: 'uppercase',
            fontSize: '50px',
          }}
        >
          {courseDetails?.specialCourseName}
        </div>
      </div>
      <div style={{ marginTop: '20px' }}>
        <h2>Katılım Koşulları</h2>
        <p>{courseDetails?.specialCourseCondition}</p>
      </div>
      <div style={{ marginTop: '20px' }}>
        <h2>Antrenörler</h2>
        <p>{courseDetails?.specialCourseDefinition}</p>
      </div>
    </div>
  );
  
}
