import React, { useEffect, useState } from 'react';
import ResponsiveAppBar from '../components/appbar';
import agent from '../api/agent';
import Box from '@mui/material/Box';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';

export default function Prices() {
  const [prices, setPrices] = useState([]);
  const [paymentTypes, setPaymentTypes] = useState([]);
  const [selectedPaymentType, setSelectedPaymentType] = useState('');

  useEffect(() => {
    agent.Prices.list()
      .then(prices => setPrices(prices))
      .catch(error => console.error('Error fetching prices:', error));
  }, []);

  useEffect(() => {
    agent.Prices.paymentDetail()
      .then(paymentTypes => setPaymentTypes(paymentTypes))
      .catch(error => console.error('Error fetching payment types:', error));
  }, []);

  const handlePaymentTypeChange = (event) => {
    setSelectedPaymentType(event.target.value);
  };

  const filteredPrices = prices.filter(data => {
    // Assuming PaymentTypeId is a property in the data object
    return selectedPaymentType === '' || data.PaymentTypeId === selectedPaymentType;
  });

  return (
    <div>
      <ResponsiveAppBar />
      <Box sx={{ minWidth: 120, marginBottom: 2 }}>
        <FormControl fullWidth>
          <InputLabel id="payment-type-select-label">Payment Type</InputLabel>
          <Select
            labelId="payment-type-select-label"
            id="payment-type-select"
            value={selectedPaymentType}
            label="Payment Type"
            onChange={handlePaymentTypeChange}
          >
            <MenuItem value="">All</MenuItem>
            {paymentTypes.map(paymentType => (
              <MenuItem key={paymentType.id} value={paymentType.id}>
                {paymentType.PaymentTypeName}
              </MenuItem>
            ))}
          </Select>
        </FormControl>
      </Box>

      <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-around' }}>
        {filteredPrices.length > 0 ? (
          filteredPrices.map(data => (
            <p key={data.id}>{data.price}</p>
          ))
        ) : (
          <p>No prices available</p>
        )}
      </div>
    </div>
  );
}
